mapper.update:
update icm_admin_authority_rl set atv_flag=1, oper_time = now()
where atv_flag = 0


		and owner_code = #{roleCode}


		and owner_code = #{username}


AND tenant_id = #{tenantId}


update icm_admin_authority_rl set atv_flag=0, oper_time = now()
where atv_flag = 1


		and owner_code = #{roleCode}


		and owner_code = #{username}



	and menu_code in

		#{item}


AND tenant_id = #{tenantId}
