mapper.select:
select 

from icm_admin_department as t




	order by ${page.orderBy} ${page.orderType} 

limit #{page.offset},#{page.rows}

select 
	id
from icm_admin_department as t 
where t.atv_flag = 1
AND tenant_id = #{tenantId}
AND t.id != #{id}
AND t.code = #{code}
limit 1

select 

from icm_admin_department as t 
where t.atv_flag = 1
	AND t.tenant_id = #{tenantId}

	AND t.code = #{code}


	AND t.id = #{id}

limit 1

mapper.update:
update icm_admin_department set atv_flag=0, oper_time = now()
where id=#{id} and atv_flag = 1
AND tenant_id = #{tenantId}
