mapper.select:
select count(1) as count
from icm_admin_dict as t
where t.parent_id = ${@DictEntity@DEFAULT_PARENT_ID}

select tt.name as tenantName, t1.code as parentCode, t1.name as parentName,
from icm_admin_dict as t left join icm_admin_tenant as tt on t.tenant_id = tt.tenant_id and tt.atv_flag = 1 left join icm_admin_dict as t1 on t.parent_id = t1.id and t1.atv_flag = 1 and t1.parent_id = ${@DictEntity@DEFAULT_PARENT_ID} AND t1.tenant_id = t.tenant_id
order by t.parent_id, t.code, t.oper_time desc limit #{page.offset},#{page.rows}

		select 

		from icm_admin_dict as t 
		where t.atv_flag = 1 

            AND t.id = #{id}


            AND t.code = #{code}


            AND t.parent_id = #{parentId}


			AND t.tenant_id = #{tenantId}

		limit 1

mapper.update:
update icm_admin_dict set atv_flag=0, oper_time = now()
where id=#{id} and atv_flag = 1

	AND tenant_id = #{tenantId}
