mapper.sql:

            AND t.service_id = #{obj.serviceId}

        AND t.tenant_id = #{obj.tenantId}

            AND t.module_name like CONCAT('%', #{obj.moduleName}, '%')


            AND t.operation_name like CONCAT('%', #{obj.operationName}, '%')


            AND t.ip_address = #{obj.ipAddress}


            AND (t.oper_user = #{obj.operUser} or t.oper_name like CONCAT('%', #{obj.operUser}, '%'))


            AND t.oper_time >= #{obj.operTimeStart}


            AND t.oper_time <= #{obj.operTimeEnd}

		AND t.atv_flag = 1

mapper.select:
select t1.name as tenantName,
from icm_admin_oper_log as t left join icm_admin_tenant as t1 on t.tenant_id = t1.tenant_id
order by t.oper_time desc limit #{page.offset},#{page.rows}
