		from icm_admin_param as t 
		where t.atv_flag = 1

            AND t.id = #{id}


            AND t.code = #{code}


			AND tenant_id = #{tenantId}

		limit 1

mapper.update:
update icm_admin_param set atv_flag=0, oper_time = now()
where id=#{id} and atv_flag = 1

	AND tenant_id = #{tenantId}
