mapper.select:
select
from icm_admin_user as t left JOIN icm_admin_login_user as t1 on t.username = t1.username AND t1.atv_flag =1 AND t1.tenant_id = t.tenant_id
AND t1.status = #{obj.status}

mapper.sql:

	AND t.username like concat('%', #{obj.username}, '%')


	AND (t.name like concat('%', #{obj.name}, '%')
	or t.username like concat('%', #{obj.name}, '%'))


	AND t.phone like concat('%', #{obj.phone}, '%')


	AND t.email like concat('%', #{obj.email}, '%')


	AND t.join_date = #{obj.joinDate} 


	AND t.origins = #{obj.origins}


	AND t.oper_user = #{obj.operUser} 


	AND t.oper_name = #{obj.operName} 


	AND t.tenant_id = #{obj.tenantId}

AND t.atv_flag =1

mapper.select:
select t1.status, t2.department_code as departCode,
from icm_admin_user as t left JOIN icm_admin_login_user as t1 on t.username = t1.username AND t1.atv_flag =1 AND t1.tenant_id = t.tenant_id left join icm_admin_department_user_rl as t2 on t.username = t2.username and t2.atv_flag =1 AND t2.tenant_id = t.tenant_id
AND t1.status = #{obj.status}
order by t.oper_time desc limit #{page.offset},#{page.rows}

select t1.status, t2.department_code as departCode, t3.post_id as postId,
from icm_admin_user as t left JOIN icm_admin_login_user as t1 on t.username = t1.username AND t1.atv_flag =1 AND t1.tenant_id = t.tenant_id left JOIN icm_admin_department_user_rl as t2 on t.username = t2.username and t2.atv_flag =1 AND t2.tenant_id = t.tenant_id left JOIN icm_admin_user_post_rl as t3 on t.username = t3.username and t3.atv_flag =1 AND t3.tenant_id = t.tenant_id where t.atv_flag = 1
and t.id=#{id}
and t.username = #{username}
limit 1

mapper.update:
update icm_admin_user 


		portrait = #{portrait},


where username = #{username}
and atv_flag = 1

	AND tenant_id = #{tenantId}

update icm_admin_user set atv_flag=0, oper_time = now()
where username=#{username} 
and atv_flag = 1

	AND tenant_id = #{tenantId}

mapper.select:
select t.username as username, t.name as name, DATE_FORMAT(t1.oper_time,'%Y-%m-%d %H:%i:%s') as operTime, t1.oper_user as operUser, t1.oper_name as operName from icm_admin_user as t left JOIN icm_admin_department_user_rl t1 on t.username = t1.username and t1.atv_flag =1 AND t1.tenant_id = t.tenant_id where t.atv_flag =1 and t1.department_code = #{obj.departmentCode} AND t.tenant_id = #{obj.tenantId}
AND (t.username like concat('%', #{obj.name}, '%') or t.name like concat('%', #{obj.name}, '%'))
order by ${page.orderBy} ${page.orderType}
limit #{page.offset},#{page.rows}

select t.username as username, t.name as name from icm_admin_user as t where t.atv_flag =1 and not exists (select 1 from icm_admin_department_user_rl t1 where t.username = t1.username and t1.atv_flag =1 AND t1.tenant_id = t.tenant_id ) AND t.tenant_id = #{obj.tenantId}
AND (t.username like concat('%', #{obj.name}, '%') or t.name like concat('%', #{obj.name}, '%'))
order by ${page.orderBy} ${page.orderType}
limit #{page.offset},#{page.rows}

select t.username as username, t.name as name, DATE_FORMAT(t1.oper_time,'%Y-%m-%d %H:%i:%s') as operTime, t1.oper_user as operUser, t1.oper_name as operName from icm_admin_user as t left JOIN icm_admin_user_role_rl t1 on t.username = t1.username and t1.atv_flag =1 AND t1.tenant_id = t.tenant_id where t.atv_flag =1 and t1.role_code = #{obj.roleCode} AND t.tenant_id = #{obj.tenantId}
AND (t.username like concat('%', #{obj.name}, '%') or t.name like concat('%', #{obj.name}, '%'))
order by ${page.orderBy} ${page.orderType}
limit #{page.offset},#{page.rows}

select 
	t.username as username,
	t.name as name
from icm_admin_user as t
where t.atv_flag =1 
	and not exists (select 1 from icm_admin_user_role_rl t1
		where t.username = t1.username
		and t1.role_code = #{obj.roleCode}
		and t1.atv_flag =1
		AND t1.tenant_id = t.tenant_id 
	)
	AND t.tenant_id = #{obj.tenantId}

		AND (t.username like concat('%', #{obj.name}, '%')
		or t.name like concat('%', #{obj.name}, '%'))


	order by ${page.orderBy} ${page.orderType}

limit #{page.offset},#{page.rows}

select t.username as username, t.name as name, t1.type as type, DATE_FORMAT(t1.oper_time,'%Y-%m-%d %H:%i:%s') as operTime, t1.oper_user as operUser, t1.oper_name as operName from icm_admin_user as t left join icm_admin_group_user t1 on t.username = t1.username and t1.atv_flag =1 AND t1.tenant_id = t.tenant_id where t.atv_flag =1 and t1.group_code = #{obj.groupCode} AND t.tenant_id = #{obj.tenantId}
AND t1.type = #{obj.groupUserType}
AND (t.username like concat('%', #{obj.name}, '%') or t.name like concat('%', #{obj.name}, '%'))
order by ${page.orderBy} ${page.orderType}
limit #{page.offset},#{page.rows}

select t.username as username, t.name as name from icm_admin_user as t where t.atv_flag =1 and not exists (select 1 from icm_admin_group_user t1 where t.username = t1.username and t1.atv_flag =1 AND t1.tenant_id = t.tenant_id ) AND t.tenant_id = #{obj.tenantId}
AND (t.username like concat('%', #{obj.name}, '%') or t.name like concat('%', #{obj.name}, '%'))
order by ${page.orderBy} ${page.orderType}
limit #{page.offset},#{page.rows}
