mapper.select:
select 

from icm_admin_user_post_rl as t




	order by ${page.orderBy} ${page.orderType} 

limit #{page.offset},#{page.rows}

select 
	id
from icm_admin_user_post_rl as t 
where t.atv_flag = 1
AND tenant_id = #{tenantId}
AND t.username = #{username}
AND t.post_id = #{postId}
limit 1

mapper.update:
